CREATE TABLE IF NOT EXISTS article_act_code
(
    article_act_code character varying(5)     PRIMARY KEY                        ,
    description      character varying(100)                              NOT NULL,
    effective_date   date                     DEFAULT now()              NOT NULL,
    expiry_date      date                     DEFAULT '9999-12-31'::date NOT NULL,
    update_timestamp timestamp with time zone DEFAULT now()              NOT NULL
);


-- Comments
COMMENT ON TABLE article_act_code IS 'Contains a list of values used to indicate an Act or Regulation. I.e. MA - Mines Act, HSRCM - Health, Safety and Reclamation Code for Mines in BC.';
COMMENT ON COLUMN article_act_code.description IS 'This is the legal name of the Act or Regulation.';
COMMENT ON COLUMN article_act_code.effective_date IS 'Effective Date reflects the date the Act or Regulation is legally enacted.';
COMMENT ON COLUMN article_act_code.expiry_date IS 'Expiry Date reflects the legal date an Act or Regulation is no longer valid. 9999-12-31 implies the code value is still valid.';


